const ctx = $('ğŸ”— ĞĞ±ÑŠĞµĞ´Ğ¸Ğ½ĞµĞ½Ğ¸Ğµ Ğ´Ğ°Ğ½Ğ½Ñ‹Ñ…').first().json;

// Build HeyGen v2 video generation request
const heygenBody = {
  video_inputs: [
    {
      character: {
        type: 'avatar',
        avatar_id: ctx.heygen_avatar_id,
        avatar_style: 'normal'
      },
      voice: {
        type: 'text',
        input_text: ctx.script_text,
        voice_id: ctx.heygen_voice_id,
        speed: 1.0
      },
      background: ctx.heygen_background
        ? { type: 'image', url: ctx.heygen_background }
        : { type: 'color', value: '#f5f5f5' }
    }
  ],
  dimension: {
    width: ctx.heygen_ratio === '16:9' ? 1920 : 1080,
    height: ctx.heygen_ratio === '16:9' ? 1080 : 1920
  },
  aspect_ratio: ctx.heygen_ratio || '9:16',
  test: false
};

return [{ json: {
  body: JSON.stringify(heygenBody),
  heygen_api_key: ctx.heygen_api_key,
  session_id: ctx.session_id
}}];

const response = $input.first().json;
