const ctx = $('ğŸµ Ğ˜Ğ·Ğ²Ğ»ĞµÑ‡ÑŒ Ğ°ÑƒĞ´Ğ¸Ğ¾ URL').first().json;

// Build A2E video generation request
const a2eBody = {
  title: ctx.title || 'Video ' + ctx.session_id,
  anchor_id: ctx.a2e_avatar_id,
  anchor_type: 0,
  audioSrc: ctx.audio_url,
  resolution: ctx.resolution || 1080,
  isSkipRs: true,
  isCaptionEnabled: ctx.captions_enabled || false,
  msg: ctx.script_text || ''
};

// Background: color (#RRGGBB) or leave default
if (ctx.background) {
  if (ctx.background.startsWith('#')) {
    // Convert #RRGGBB to [R,G,B]
    const hex = ctx.background.replace('#', '');
    a2eBody.color = [
      parseInt(hex.substring(0,2), 16),
      parseInt(hex.substring(2,4), 16),
      parseInt(hex.substring(4,6), 16)
    ];
  } else {
    a2eBody.back_id = ctx.background;
  }
}

return [{ json: {
  body: JSON.stringify(a2eBody),
  a2e_token: ctx.a2e_token,
  a2e_base_url: ctx.a2e_base_url,
  session_id: ctx.session_id
}}];

